def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )
def responsetHolder = groovyUtils.getXmlHolder( messageExchange.responseContent )

responsetHolder.namespaces['ns'] = "http://tempuri.org/"

def converionRateUSD_GBP = responsetHolder.getNodeValue("//ns:GetConversionRateResult")

log.info ("Conversion rate from USD to GBP is :" + converionRateUSD_GBP)

//Converting string to float and round to 4 decimals
float float_converionRateUSD_GBP = Float.parseFloat(converionRateUSD_GBP)
def round_converionRateUSD_GBP = new java.text.DecimalFormat('#.####').format(float_converionRateUSD_GBP)
log.info round_converionRateUSD_GBP

//Assert if the converted currency value is correct
if(round_converionRateUSD_GBP == "0.7866")
{ log.info "Pass" }
else
{ log.info "fail"}

